/* 桌面导航 */
.desktop-nav {
  display: none;
  align-items: center;
  justify-content: space-between;
  width: 100%;
}

.desktop-nav nav {
  display: flex;
  gap: 2rem;
}

.desktop-nav nav a.link {
  border-color: transparent;
  font-size: 0.95rem;
  padding-bottom: 3px;
  position: relative;
  transition: color 0.2s ease;
}

.desktop-nav nav a.link::after {
  content: "";
  position: absolute;
  width: 0;
  height: 1px;
  bottom: 0;
  left: 0;
  background-color: currentColor;
  transition: width 0.3s ease;
}

.desktop-nav nav a.link:hover::after,
.desktop-nav nav a.link.active::after {
  width: 100%;
}

/* 移动导航 */
.mobile-nav {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  position: relative;
}

/* 扁平化汉堡菜单按钮 */
#hamburger {
  background: none;
  border: none;
  cursor: pointer;
  width: 24px;
  height: 14px;
  position: relative;
  z-index: 10;
  padding: 0;
}

#hamburger span {
  display: block;
  position: absolute;
  height: 2px;
  width: 100%;
  background: currentColor;
  border-radius: 2px;
  opacity: 1;
  left: 0;
  transform-origin: center;
  transition: transform 0.3s ease,
              opacity 0.2s ease,
              top 0.3s ease;
}

#hamburger span:nth-child(1) {
  top: 0px;
}

#hamburger span:nth-child(2) {
  top: 6px;
}

#hamburger span:nth-child(3) {
  top: 12px;
}

/* 平面动画效果 */
#hamburger.open span:nth-child(1) {
  top: 6px;
  transform: rotate(45deg);
}

#hamburger.open span:nth-child(2) {
  opacity: 0;
  transform: translateX(-10px);
}

#hamburger.open span:nth-child(3) {
  top: 6px;
  transform: rotate(-45deg);
}

/* 移动菜单 */
.mobile-menu {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background: rgba(255, 255, 255, 0);
  animation: fadeInBackground 0.8s ease forwards;
  z-index: 9;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

/* 菜单容器 */
.menu-container {
  padding: 2rem;
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
}

/* 超炫酷菜单项动画 */
.menu-item {
  display: block;
  font-size: 1.8rem;
  font-weight: 500;
  margin-bottom: 2.5rem; /* 增加垂直间距 */
  opacity: 0;
  position: relative;
  text-align: center;
  /* 固定起点为汉堡按钮的右上角位置 */
  transform: translate(calc(50vw - 50%), calc(-50vh + 2rem)) scale(0.2) rotate(60deg);
  filter: blur(10px);
  animation: flyInFromHamburger 0.8s cubic-bezier(0.19, 1, 0.22, 1) forwards;
  letter-spacing: 0.05em;
}

/* 菜单项悬停效果 */
.menu-item:hover {
  transform: scale(1.05);
  transition: transform 0.3s ease;
}

/* 背景渐入动画 */
@keyframes fadeInBackground {
  0% {
    background: rgba(255, 255, 255, 0);
  }
  100% {
    background: rgba(255, 255, 255, 0.98);
  }
}

/* 从汉堡位置飞入的炫酷动画 */
@keyframes flyInFromHamburger {
  0% {
    opacity: 0;
    transform: translate(calc(50vw - 50%), calc(-50vh + 2rem)) scale(0.2) rotate(60deg);
    filter: blur(10px);
    letter-spacing: -0.5em;
  }
  10% {
    opacity: 0.1;
  }
  60% {
    opacity: 0.8;
    transform: translate(0, 0) scale(1.1) rotate(-5deg);
    filter: blur(0);
    letter-spacing: 0.1em;
  }
  80% {
    letter-spacing: 0.03em;
  }
  100% {
    opacity: 1;
    transform: translate(0, 0) scale(1) rotate(0deg);
    filter: blur(0);
    letter-spacing: 0.05em;
  }
}

